// src/components/Home.js
import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = ({ dogs }) => (
  <div>
    <h2>Bienvenue sur notre site de vente de chiens !</h2>
    <p>Découvrez nos adorables chiens disponibles à l'adoption :</p>
    <ul className="home-dog-list">
      {dogs.map((dog, index) => (
        <li key={index} className="home-dog-item">
          <Link to={`/dogs/${index}`} className="home-dog-link">
            <img
              src={`/images/${dog.image}`}
              alt={dog.name}
              className="home-dog-image"
            />
            <div className="home-dog-details">
              <span className="home-dog-name">{dog.name}</span>
              <span className="home-dog-breed">{dog.breed}</span>
              <span className="home-dog-price">${dog.price}</span>
              <p className="home-dog-description">{dog.description}</p>
            </div>
          </Link>
          <div className="home-dog-actions">
            <button
              className="buy-now-button"
              onClick={() => handleBuyNowClick(index)}
            >
              Acheter maintenant
            </button>
            <Link to={`/dogs/${index}`}>
              <button
                className="more-info-button"
                onClick={() => handleMoreInfoClick(index)}
              >
                Plus d'informations
              </button>
            </Link>
          </div>
        </li>
      ))}
    </ul>
  </div>
);

export default Home;
