import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './DogList.css';
import DogPopup from './DogPopup'; // Importez le composant DogPopup

const DogList = ({ dogs }) => {
  const [selectedDog, setSelectedDog] = useState(null); // Nouvel état pour le chien sélectionné

  const handleBuyNowClick = (index) => {
    console.log(`Acheter maintenant le chien ${index + 1}`);
  };

  const handleMoreInfoClick = (index) => {
    // Mettez à jour l'état du chien sélectionné lors du clic sur "Plus d'informations"
    setSelectedDog(dogs[index]);
  };

  const closePopup = () => {
    // Fermer le popup en réinitialisant l'état du chien sélectionné à null
    setSelectedDog(null);
  };

  return (
    <div>
      <h2>Voici notre sélection:</h2>
      <div className="dog-list">
        {dogs.map((dog, index) => (
          <div key={index} className="dog-item">
            <Link to={`/dogs/${index}`}>
              <img
                src={`/images/${dog.image}`}
                alt={dog.name}
                className="dog-image"
              />
            </Link>
            <div className="dog-details">
              <div>
                <span className="dog-name">{dog.name}</span>
                <span className="dog-breed">{dog.breed}</span>
              </div>
              <div>
                <span className="dog-price">${dog.price}</span>
                <p className="dog-caption">{dog.caption}</p>
              </div>
            </div>
            <div className="action-buttons">
              <Link to={`/achat`}>
                <button className="buy-now-button" onClick={() => handleBuyNowClick(index)}>
                  Acheter maintenant
                </button>
              </Link>
              {/* Utilisez handleMoreInfoClick pour afficher le popup */}
              <button className="more-info-button" onClick={() => handleMoreInfoClick(index)}>
                Plus d'informations
              </button>
            </div>
          </div>
        ))}
      </div>
      {/* Afficher le popup si un chien est sélectionné */}
      {selectedDog && <DogPopup dog={selectedDog} onClose={closePopup} />}
    </div>
  );
};

export default DogList;
